import React from 'react';
import './App.css';

class App extends React.Component {
  handleClick() {
    console.log('Clicou no botão!')
  }

  constructor() {
    super()
    this.handleClick = this.handleClick.bind(this);
  }

  render() {
    return <div>
      <button onClick={this.handleClick}>Meu botão</button>
      <button onClick={this.handleClick}>Meu botão</button>
      <button onClick={this.handleClick}>Meu botão</button>
    </div>
  }
}

export default App;
