import React from 'react';
import './App.css';

class App extends React.Component {
  handleClick() {
    console.log('Clicou no botão!')
  }

  constructor() {
    super()
    this.handleClick = this.handleClick.bind(this);
  }

  render() {
    return <button onClick={this.handleClick}>Meu botão</button>
  }
}

export default App;
