    this.handleClick1 = this.handleClick1.bind(this);
    this.handleClick2 = this.handleClick2.bind(this);
    this.handleClick3 = this.handleClick3.bind(this);
    this.state = {
      numeroDeCliques1: 0,
      numeroDeCliques2: 0,
      numeroDeCliques3: 0,
    }
  }

  handleClick1 = () => {
    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques1: estadoAnterior.numeroDeCliques1 + 1
    }))
    console.log(this.state.numeroDeCliques1);
  }

  handleClick2 = () => {
    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques2: estadoAnterior.numeroDeCliques2 + 1
    }))
    console.log(this.state.numeroDeCliques2);
  }

  handleClick3 = () => {
    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques3: estadoAnterior.numeroDeCliques3 + 1
    }))
    console.log(this.state.numeroDeCliques3);
  }

  changeBackColor (number) {
    console.log(this.state.numeroDeCliques1);
    return number % 2 === 0 ? 'green' : 'brown'
  }

  render() {
    console.log(this.state.numeroDeCliques1);
    return <div>
      <button style={ { backgroundColor: this.changeBackColor(this.state.numeroDeCliques1) } } onClick={this.handleClick1}>Meu botão</button>
      <button onClick={this.handleClick2}>Meu botão</button>
      <button onClick={this.handleClick3}>Meu botão</button>
    </div>
  }
}

export default App;
